import React, { useEffect } from 'react'
import "./chatOnline.css"

export default function ChatOnline({onlineUsers, currentId, setCurrentChat}) {

  const [friends, setFriends]= useState([]);
  const [onlineFriends, setOnlineFriends] = useState([])

  useEffect(()=>{
    const getFriends = async ()=>{
      const res = await axios.get("/users/friends/" + currentId);
      setFriends(res.data)
    };
    getFriends()
  }, [currentId])

  useEffect(()=>{
    setOnlineFriends(friends.filter((f)=> onlineFriends.includes(f._id)))
    
  }, [friends, onlineUsers]);

  return (
    <div className='chatOnline'>
      <div className="chatOnlineFriend">
        <div className="chatOnlineFriendImgContainer">

          <img src="/assets/f1.jpg" alt="" className="chatOnlineFriendImg" />

          <div className="chatOnlineBadge"></div>
        </div>

          <span className="chatOnlineName">Nobel Ola</span>
      </div>
      <div className="chatOnlineFriend">
        <div className="chatOnlineFriendImgContainer">

          <img src="/assets/f1.jpg" alt="" className="chatOnlineFriendImg" />

          <div className="chatOnlineBadge"></div>
        </div>

          <span className="chatOnlineName">Nobel Ola</span>
      </div>
      <div className="chatOnlineFriend">
        <div className="chatOnlineFriendImgContainer">

          <img src="/assets/f1.jpg" alt="" className="chatOnlineFriendImg" />

          <div className="chatOnlineBadge"></div>
        </div>

          <span className="chatOnlineName">Nobel Ola</span>
      </div>
    </div>
  )
}
